import React from 'react'
import Header from '../../components/Header/Header'
import Footer from '../../components/Footer/Footer'
import s from './HomePage.css'

import { useEffect, useState } from "react";
import { getAllByAltText, render } from '@testing-library/react';

export default function HomePage() {

  const [categories, setCategories] = useState([]);

  useEffect( () => {

    fetch('http://localhost:3333/categories/all')
      .then( resp => resp.json())
      .then(data => setCategories(data));
  })
  

  return (
    <div>
        <div className='banner'>
          <h1 class="text" className='t1'>Распродажа</h1>
          <h2 class="text" className='t2'>в честь нового сезона</h2>
          <ul className='buttons'>
            <a className='sales'>Все акции</a>
            <a className='about'>Подробнее</a>
          </ul>
          <img width="900" height="679" src='E:\Documents\Visual\Project\telran_front_end1\src\Pages\HomePage\Banner\png.png'></img>
        </div>
        <div className='category'>
          <h1>Категории</h1>
          {categories.map((item) => (
            <li key={item.name}>
              {item.title}
              <img width="150" height= "150" src={item.image}/>
            </li>
          ))}
        </div>
        <div>
          Акции
        </div>
    </div>
    
  )
}
